                    <SelectValue placeholder="Language" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="english">English</SelectItem>
                    <SelectItem value="arabic">ÿßŸÑÿπÿ±ÿ®Ÿäÿ© (Arabic)</SelectItem>
                    <SelectItem value="bengali">‡¶¨‡¶æ‡¶Ç‡¶≤‡¶æ (Bengali)</SelectItem>
                  </SelectContent>
