import { useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Switch } from "@/components/ui/switch";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Label } from "@/components/ui/label";
import TopBar from "@/components/top-bar";
import { Settings as SettingsIcon, Bell, MapPin, Calculator, Palette, Download, Smartphone, AlertCircle } from "lucide-react";
import { 
  requestNotificationPermission, 
  getNotificationPermissionStatus,
  getNotificationPreferences,
  saveNotificationPreferences,
  clearPrayerNotifications
} from "@/lib/notifications";

export default function SettingsPage() {
  const [notifications, setNotifications] = useState(true);
  const [calculationMethod, setCalculationMethod] = useState("ISNA");
  const [darkMode, setDarkMode] = useState(false);
  const [deferredPrompt, setDeferredPrompt] = useState<any>(null);
  const [isInstallable, setIsInstallable] = useState(false);
  const [notificationPermission, setNotificationPermission] = useState("default");
  const [reminderMinutes, setReminderMinutes] = useState(15);
  const [soundEnabled, setSoundEnabled] = useState(true);

  useEffect(() => {
    const handler = (e: Event) => {
      e.preventDefault();
      setDeferredPrompt(e);
      setIsInstallable(true);
    };

    window.addEventListener('beforeinstallprompt', handler);

    const prefs = getNotificationPreferences();
    setNotifications(prefs.prayerNotifications);
    setReminderMinutes(prefs.reminderMinutes);
    setSoundEnabled(prefs.soundEnabled);
    setNotificationPermission(getNotificationPermissionStatus());

    return () => window.removeEventListener('beforeinstallprompt', handler);
  }, []);

  const handleRequestNotificationPermission = async () => {
    const granted = await requestNotificationPermission();
    if (granted) {
      setNotificationPermission("granted");
      window.dispatchEvent(new CustomEvent("rescheduleNotifications"));
      alert("Notification permission granted! You will now receive prayer time notifications.");
    } else {
      alert("Notification permission denied. Please enable notifications in your browser settings.");
    }
  };

  const handleNotificationToggle = (checked: boolean) => {
    setNotifications(checked);
    saveNotificationPreferences({ prayerNotifications: checked });

    if (!checked) {
      clearPrayerNotifications();
    } else {
      window.dispatchEvent(new CustomEvent("rescheduleNotifications"));
    }
  };

  const handleReminderMinutesChange = (value: string) => {
    const minutes = parseInt(value);
    setReminderMinutes(minutes);
    saveNotificationPreferences({ reminderMinutes: minutes });
    window.dispatchEvent(new CustomEvent("rescheduleNotifications"));
  };

  const handleSoundToggle = (checked: boolean) => {
    setSoundEnabled(checked);
    saveNotificationPreferences({ soundEnabled: checked });
  };

  const handleInstallClick = async () => {
    if (!deferredPrompt) {
      alert('Installation not available. You may have already installed the app or your browser does not support installation.');
      return;
    }

    deferredPrompt.prompt();
    const { outcome } = await deferredPrompt.userChoice;

    if (outcome === 'accepted') {
      setDeferredPrompt(null);
      setIsInstallable(false);
    }
  };

  const handleDownloadAPK = () => {
    const currentUrl = window.location.origin;
    const downloadUrl = `/download/apk`;
    window.open(downloadUrl, '_blank');
  };

  const calculationMethods = [
    { value: "ISNA", label: "Islamic Society of North America (ISNA)" },
    { value: "MWL", label: "Muslim World League" },
    { value: "EGYPT", label: "Egyptian General Authority of Survey" },
    { value: "MAKKAH", label: "Umm Al-Qura University, Makkah" },
    { value: "KARACHI", label: "University of Islamic Sciences, Karachi" },
  ];

  const handleLocationPermission = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        () => alert("Location permission granted"),
        () => alert("Location permission denied")
      );
    }
  };

  return (
    <div className="flex flex-col h-screen bg-gradient-to-b from-emerald-950 to-emerald-900">
      <TopBar title="Settings" subtitle="Customize your Islamic companion" />

      <main className="flex-1 overflow-y-auto p-4 space-y-6">
        {/* Prayer Times Settings */}
        <Card data-testid="card-prayer-settings">
          <CardHeader>
            <CardTitle className="flex items-center">
              <Calculator className="mr-2 h-5 w-5" />
              Prayer Times
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="calculation-method">Calculation Method</Label>
              <Select value={calculationMethod} onValueChange={setCalculationMethod}>
                <SelectTrigger id="calculation-method" data-testid="select-calculation-method">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {calculationMethods.map((method) => (
                    <SelectItem key={method.value} value={method.value}>
                      {method.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </CardContent>
        </Card>

        {/* Notifications Settings */}
        <Card data-testid="card-notification-settings">
          <CardHeader>
            <CardTitle className="flex items-center">
              <Bell className="mr-2 h-5 w-5" />
              Notifications
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            {notificationPermission !== "granted" && (
              <div className="bg-amber-500/10 border border-amber-500/30 rounded-lg p-4 space-y-2">
                <div className="flex items-start gap-2">
                  <AlertCircle className="h-5 w-5 text-amber-500 mt-0.5" />
                  <div className="flex-1">
                    <p className="text-sm font-semibold text-amber-500">Enable Notifications</p>
                    <p className="text-sm text-muted-foreground">
                      Allow notifications to receive prayer time reminders
                    </p>
                  </div>
                </div>
                <Button 
                  onClick={handleRequestNotificationPermission}
                  className="w-full"
                  data-testid="button-request-notification"
                >
                  Enable Notifications
                </Button>
              </div>
            )}

            <div className="flex items-center justify-between">
              <Label htmlFor="prayer-notifications">Prayer Time Notifications</Label>
              <Switch
                id="prayer-notifications"
                checked={notifications}
                onCheckedChange={handleNotificationToggle}
                data-testid="switch-notifications"
              />
            </div>
            <p className="text-sm text-muted-foreground">
              Get notified when it's time for prayer
            </p>

            {notifications && (
              <>
                <div className="space-y-2">
                  <Label htmlFor="reminder-minutes">Reminder Before Prayer</Label>
                  <Select value={reminderMinutes.toString()} onValueChange={handleReminderMinutesChange}>
                    <SelectTrigger id="reminder-minutes" data-testid="select-reminder-minutes">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="0">No reminder</SelectItem>
                      <SelectItem value="5">5 minutes before</SelectItem>
                      <SelectItem value="10">10 minutes before</SelectItem>
                      <SelectItem value="15">15 minutes before</SelectItem>
                      <SelectItem value="30">30 minutes before</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div className="flex items-center justify-between">
                  <Label htmlFor="sound-enabled">Notification Sound</Label>
                  <Switch
                    id="sound-enabled"
                    checked={soundEnabled}
                    onCheckedChange={handleSoundToggle}
                    data-testid="switch-sound"
                  />
                </div>
              </>
            )}
          </CardContent>
        </Card>

        {/* Location Settings */}
        <Card data-testid="card-location-settings">
          <CardHeader>
            <CardTitle className="flex items-center">
              <MapPin className="mr-2 h-5 w-5" />
              Location
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <p className="text-sm text-muted-foreground">
              Location is used to calculate accurate prayer times and Qibla direction
            </p>
            <Button onClick={handleLocationPermission} data-testid="button-location-permission">
              Grant Location Permission
            </Button>
          </CardContent>
        </Card>

        {/* Appearance Settings */}
        <Card data-testid="card-appearance-settings">
          <CardHeader>
            <CardTitle className="flex items-center">
              <Palette className="mr-2 h-5 w-5" />
              Appearance
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="flex items-center justify-between">
              <Label htmlFor="dark-mode">Dark Mode</Label>
              <Switch
                id="dark-mode"
                checked={darkMode}
                onCheckedChange={setDarkMode}
                data-testid="switch-dark-mode"
              />
            </div>
            <p className="text-sm text-muted-foreground">
              Toggle between light and dark themes
            </p>
          </CardContent>
        </Card>

        {/* Download APK Section */}
        <Card data-testid="card-download-apk" className="border-amber-500/50">
          <CardHeader>
            <CardTitle className="flex items-center text-amber-500">
              <Download className="mr-2 h-5 w-5" />
              Install Android App
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <p className="text-sm text-muted-foreground">
              Get Islamic Companion on your Android device
            </p>
            <div className="flex flex-col gap-3">
              {isInstallable && (
                <div className="space-y-2">
                  <Button 
                    onClick={handleInstallClick}
                    className="w-full bg-green-600 hover:bg-green-700 text-white"
                    data-testid="button-install-pwa"
                  >
                    <Smartphone className="mr-2 h-4 w-4" />
                    One-Click Install (Recommended)
                  </Button>
                  <p className="text-xs text-muted-foreground">
                    Instant installation - just click and confirm
                  </p>
                </div>
              )}
              <div className="space-y-2">
                <Button 
                  onClick={handleDownloadAPK}
                  className="w-full bg-amber-500 hover:bg-amber-600 text-white"
                  data-testid="button-download-apk"
                  variant={isInstallable ? "outline" : "default"}
                >
                  <Download className="mr-2 h-4 w-4" />
                  {isInstallable ? "Alternative: Generate APK" : "Generate APK File"}
                </Button>
                <p className="text-xs text-muted-foreground">
                  Opens APK builder tool - follow steps to download
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* About Section */}
        <Card data-testid="card-about">
          <CardHeader>
            <CardTitle>About</CardTitle>
          </CardHeader>
          <CardContent className="space-y-2">
            <p className="text-sm text-muted-foreground">
              Islamic Companion v1.0.0
            </p>
            <p className="text-sm text-muted-foreground">
              A comprehensive Islamic app for prayer times, Qibla direction, and more.
            </p>
          </CardContent>
        </Card>
      </main>
    </div>
  );
}

