              <div className="grid grid-cols-2 gap-3">
                <div>
                  <label className="text-xs text-emerald-200 mb-1.5 block">Category</label>
                  <Select value={selectedCategory} onValueChange={setSelectedCategory}>
                    <SelectTrigger 
