// Export the complete Islamic names with auto-generated Bengali meanings
export const islamicNames: IslamicName[] = rawIslamicNames.map((rawName, index) => {
  const id = rawName.id || `name-${index + 1}`;
  const meaningBengali = rawName.meaningBengali || translateWithContext(
    rawName.meaning, 
    rawName.name, 
    rawName.origin, 
    rawName.category
  );

  return {
    id,
    name: rawName.name,
    nameArabic: rawName.nameArabic,
    nameBengali: rawName.nameBengali,
    meaning: rawName.meaning,
    meaningBengali,
    origin: rawName.origin,
    gender: rawName.gender,
    category: rawName.category || "General"
  };
});